INSERT INTO `database_vanban` VALUES (61,1734886800000,1734912000000,45984,'1446/TB-HVM','Thông báo','Nguyễn Thị Thùy','TB vv điều chỉnh quy định về tính điểm thành phần thi tốt nghiệp đại học chính quy năm 2024 của Học viện KTMM','Phòng Đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Lương Thế Dũng - Học viện Kỹ thuật mật mã',1,1),(62,1734627600000,1734998400000,45838,'1465/KH-HVM','Kế hoạch','Trịnh Đình Hợp','KH  tổ chức thi KTHP, HK1 năm học 2024-2025 lớp H35 đào tạo ĐH chuyên ngành KTMM','Phòng Khảo thí và Đảm bảo chất lượng đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Nguyễn Hiếu Minh - Học viện Kỹ thuật mật mã',2,1),(63,1734541200000,1734912000000,45749,'1459/KH-HVM','Kế hoạch','Trịnh Đình Hợp','KH  tổ chức thi kết thúc học phần HK1 năm học 2024 - 2025 Các hệ đào tạo phục vụ kinh tế xã hội (đợt 2)','Phòng Khảo thí và Đảm bảo chất lượng đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Nguyễn Hiếu Minh - Học viện Kỹ thuật mật mã',2,1),(64,1734541200000,1734652800000,45742,'72/QĐ-HĐT','Quyết định','Vũ Đức Tân','QĐ vv Thành lập ban coi thi tốt nghiệp ngành ATTT năm 2024','Phòng Khảo thí và Đảm bảo chất lượng đào tạo','Sổ Hội đồng, Ban Soạn thảo..','Thường','Thường','Hoàng Văn Thức - Học viện Kỹ thuật mật mã',2,1),(65,1734541200000,1734652800000,45720,'677/HVM-CTTC','Công văn','Nguyễn Việt Hưng','Vv xin ý kiến vào Dự thảo Quy chế hoạt động thanh tra, kiểm tra giáo dục và Quy trình tiến hành thanh tra kiểm tra giáo dục tại Học viện','Phòng Chính trị - Tổ chức','Sổ Công văn đi','Thường','Thường','Vũ Văn Vinh - Phòng Chính trị - Tổ chức',4,1),(66,1734454800000,1734912000000,45662,'1457/QĐ-HVM','Quyết định','Triệu Thị Yến','QĐ vv ban hành phiếu lấy ý kiến phản hồi việc tổ chức làm và đánh giá đồ án, đề án, luận văn tốt nghiệp','Phòng Khảo thí và Đảm bảo chất lượng đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Nguyễn Hiếu Minh - Học viện Kỹ thuật mật mã',2,1),(67,1734627600000,1734652800000,45661,'1452/KH-HVM','Kế hoạch','Phạm Ánh Tuyết','KH tổ chức Lễ  bế giảng và trao bằng tốt nghiệp học viên đào tạo đại học vừa làm vừa học chuyên ngành KTMM năm 2024 (Lớp TC24  A, B)','Phòng Đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Lương Thế Dũng - Học viện Kỹ thuật mật mã',2,1),(68,1734454800000,1734480000000,45633,'674/HVM-HC','Công văn','Đỗ Đức Tất','Vv gửi Danh sách trực chỉ huy, trực nghiệp vụ ngoài giờ hành chính năm 2025','Phòng Hành chính','Sổ Công văn đi','Thường','Thường','Nguyễn Tân Đăng - Học viện Kỹ thuật mật mã',2,1),(69,1734282000000,1734307200000,45369,'667/HVM-CTTC','Công văn','Nguyễn Việt Hưng','V/v kê khai, thu nộp, công khai bản kê khai tài sản, thu nhập năm 2024 và kiểm soát tài sản thu nhập năm 2025','Phòng Chính trị - Tổ chức','Sổ Công văn đi','Thường','Thường','Vũ Văn Vinh - Phòng Chính trị - Tổ chức',7,1),(70,1734022800000,1734048000000,45259,'1405/QĐ-HVM','Quyết định','Bùi Thị Tuyết Nhung','Quyết định điều chỉnh 01 Hội đồng chấm đồ án tốt nghiệp đại học chính quy năm 2024, Ngành An toàn thông tin, Khoá 17 (AT17)\n','Phòng Đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Lương Thế Dũng - Học viện Kỹ thuật mật mã',2,1),(71,1733936400000,1733961600000,45143,'1383/KH-HVM','Kế hoạch','Nguyễn Thị Thùy','KH tổ chức luyện tập và tham gia thi đấu Giải thể thao chào mừng kỷ niệm 80 năm Ngày thành lập Quân đội nhân dân Việt Nam và 35 năm Ngày hội Quốc phòng toàn dân của Ban Cơ yếu Chính phủ ','Phòng Chính trị - Tổ chức','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Hoàng Văn Thức - Học viện Kỹ thuật mật mã',1,1),(72,1733936400000,1733961600000,45142,'659/HVM-HC','Công văn','Đào Hồng Nam','V/v đề xuất xây dựng văn bản quy phạm nội bộ của Ban Cơ yếu Chính phủ năm 2025\n','Phòng Hành chính','Sổ Công văn đi','Thường','Thường','Đào Hồng Nam - Phòng Hành chính',2,1),(73,1733763600000,1733788800000,44959,'655/HVM-ĐT','Công văn','Nguyễn Quang Quân','V/v lấy ý kiến đóng góp bản dự thảo Quy định về định mức giờ chuẩn giảng dạy và nghiên cứu khoa học đối với giảng viên.','Phòng Đào tạo','Sổ Công văn đi','Thường','Thường','Hoàng Sỹ Tương - Phòng Đào tạo',4,1),(74,1733677200000,1733702400000,44806,'427-KH/ĐUH','Kế hoạch','Nguyễn Thị Thùy','Tài liệu Hướng dẫn 1277-HD/ĐUB  kèm theo số 427-KH/ĐUH KH tổ chức Đại hội Đảng các cấp trong Đảng bộ Học viện KTMM tiến tới ĐHĐB toàn quốc lần thứ XIV của Đảng ','Phòng Chính trị - Tổ chức','Sổ Đảng ủy Học viện','Thường','Thường','Nguyễn Tân Đăng - Học viện Kỹ thuật mật mã',1,1),(75,1733418000000,1733443200000,44692,'1350/KH-HVM','Kế hoạch','Nguyễn Thị Thùy','KH Tổ chức triển khai thực đồ án tốt nghiệp đồ án tốt nghiệp cho sinh viên viên đại học chính quy Khoa 4 (DT4 - đợt 2), ngành Kỹ thuật Điện tử - Viễn thông ','Phòng Đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Lương Thế Dũng - Học viện Kỹ thuật mật mã',1,1),(76,1733072400000,1733097600000,44237,'1316/TB-HVM','Thông báo','Nguyễn Thị Thùy','TB điều chỉnh kế hoạch tổ chức triển khai thực hiện đồ án tốt nghiệp cho học viện đại học vừa làm vừa học, lớp TC24A,B - Khoa 24, chuyên ngành KTMM','Phòng Đào tạo','Sổ QĐ, BC, KH, TTr..','Thường','Thường','Lương Thế Dũng - Học viện Kỹ thuật mật mã',1,1);
